package net.jeeshop.services.manage.orderpay.impl;import java.util.List;import javax.annotation.Resource;import net.jeeshop.core.ServersManager;import net.jeeshop.services.manage.orderpay.OrderpayService;import net.jeeshop.services.manage.orderpay.bean.Orderpay;import net.jeeshop.services.manage.orderpay.dao.OrderpayDao;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.stereotype.Service;@Service("orderpayServiceManage")public class OrderpayServiceImpl extends ServersManager<Orderpay, OrderpayDao> implements		OrderpayService {    @Resource(name = "orderpayDaoManage")    @Override    public void setDao(OrderpayDao orderpayDao) {        this.dao = orderpayDao;    }        @Resource(name="jdbcTemplate")	private JdbcTemplate jdbcTemplate;        /**     * 查询在线支付记录     * @param orderId     * @return     */    public List<?> getPayList(String orderId){    	StringBuffer sb = new StringBuffer(32);    	sb.append("select '支付宝' payType,date_format(createtime,'%y-%m-%d %h:%i:%s') as createtime,paystatus,tradeNo from t_order_alipay where orderId =? ")    	.append(" union all ")    	.append("select concat('银联:',ifnull(remark,'')) payType,date_format(createtime,'%y-%m-%d %h:%i:%s') as createtime,paystatus,tradeNo from t_order_unionpay where orderId =? ");    	    	List ls = jdbcTemplate.queryForList(sb.toString(), new Object[]{orderId,orderId});    	return ls;    }                    }